                    )}
                  />

                  <FormField
                    control={form.control}
                    name="confirmPassword"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Confirm Password</FormLabel>
                        <FormControl>
                          <div className="relative">
                            <Input 
                              type={showConfirmPassword ? "text" : "password"}
                              placeholder="Confirm your password"
                              {...field}
                            />
                            <Button
                              type="button"
                              variant="ghost"
                              size="sm"
                              className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                              onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                            >
                              {showConfirmPassword ? (
                                <EyeOff className="h-4 w-4" />
                              ) : (
                                <Eye className="h-4 w-4" />
                              )}
                            </Button>
                          </div>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <Button
                    type="submit"
                    disabled={signupMutation.isPending}
                    className="w-full bg-primary hover:bg-primary/90 text-white py-3 text-lg font-medium"
                    size="lg"
                  >
                    {signupMutation.isPending ? "Creating Account..." : "Create Account"}
                  </Button>
                </form>
              </Form>

              {/* Login Link */}
              <div className="text-center">
                <p className="text-sm text-gray-600">
                  Already have an account?{" "}
                  <Link href="/login" className="text-primary hover:underline font-medium">
                    Sign in here
                  </Link>
                </p>
              </div>

              {/* Info Text */}
              <p className="text-xs text-gray-500 text-center">
                By creating an account, you agree to our Terms of Service and Privacy Policy
              </p>
            </CardContent>
          </Card>
        </div>

        {/* Bottom CTA */}
        <div className="text-center bg-white rounded-lg p-6 shadow-sm">
          <p className="text-sm text-gray-600 mb-2">
            Questions about compliance requirements?
          </p>
          <p className="text-sm">
            Contact our compliance experts at{" "}
            <a href="mailto:compliance@secure2send.com" className="text-primary hover:underline">
              compliance@secure2send.com
            </a>
          </p>
        </div>
      </div>
    </div>
  );
}

client/src/components/admin/review-queue.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { DOCUMENT_TYPES } from "@/lib/constants";
import DocumentReviewModal from "../documents/document-review-modal";
import { Eye, CheckCircle, XCircle, Download, FileText, Filter } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import type { DocumentWithClient } from "@shared/schema";

export default function ReviewQueue() {
  const [selectedDocument, setSelectedDocument] = useState<DocumentWithClient | null>(null);
  const [statusFilter, setStatusFilter] = useState("all");
  const [clientFilter, setClientFilter] = useState("all");

  const { data: documents = [], isLoading } = useQuery<DocumentWithClient[]>({
    queryKey: ["/api/documents"],
  });

  // Get unique clients for the filter dropdown
  const uniqueClients = documents.reduce((acc: any[], doc) => {
    const existingClient = acc.find(client => client.id === doc.client.id);
    if (!existingClient && doc.client) {
      acc.push(doc.client);
    }
    return acc;
  }, []);

  const filteredDocuments = documents.filter((doc) => {
    const statusMatch = statusFilter === "all" || doc.status === statusFilter.toUpperCase();
    const clientMatch = clientFilter === "all" || doc.client.id === clientFilter;
    return statusMatch && clientMatch;
  });

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'APPROVED':
        return (
          <Badge className="bg-green-100 text-green-800 border-green-200">
            Approved
          </Badge>
        );
      case 'PENDING':
        return (
          <Badge className="bg-yellow-100 text-yellow-800 border-yellow-200">
            Pending
          </Badge>
        );
      case 'REJECTED':
        return (
          <Badge className="bg-red-100 text-red-800 border-red-200">
            Rejected
          </Badge>
        );
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  const getDocumentTypeName = (type: string) => {
    return DOCUMENT_TYPES[type as keyof typeof DOCUMENT_TYPES]?.name || type;
  };

  const getInitials = (firstName?: string, lastName?: string) => {
    if (!firstName && !lastName) return "U";
    return `${firstName?.[0] || ""}${lastName?.[0] || ""}`.toUpperCase();
  };

  const getClientDisplayName = (client: any) => {
    if (!client?.user) return 'Unknown Client';
    const { firstName, lastName, companyName, email } = client.user;
    if (companyName) return companyName;
    if (firstName || lastName) return `${firstName || ''} ${lastName || ''}`.trim();
    return email || 'Unknown Client';
  };

  const handleDownload = async (document: any) => {
    try {
      const response = await fetch(`/api/documents/${document.id}/download`, {
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error('Download failed');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = window.document.createElement('a');
      a.href = url;
      a.download = document.originalName;
      window.document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      window.document.body.removeChild(a);
    } catch (error) {
      console.error('Download error:', error);
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="animate-pulse space-y-4">
            <div className="h-4 bg-gray-200 rounded w-1/4"></div>
            <div className="space-y-3">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="h-16 bg-gray-200 rounded"></div>
              ))}
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <>
      <Card className="hover:shadow-lg transition-shadow">
        <div className="p-6 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <h3 className="text-xl font-semibold text-gray-900">Document Review Queue</h3>
            <div className="flex items-center space-x-3">
              <Select value={clientFilter} onValueChange={setClientFilter}>
                <SelectTrigger className="w-48">
                  <SelectValue placeholder="Filter by client" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Clients</SelectItem>
                  {uniqueClients.map((client) => (
                    <SelectItem key={client.id} value={client.id}>
                      <div className="flex items-center gap-2">
                        <Avatar className="h-4 w-4">
                          <AvatarFallback className="text-xs">
                            {getInitials(client.user?.firstName, client.user?.lastName)}
                          </AvatarFallback>
                        </Avatar>
                        {getClientDisplayName(client)}
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-48">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Documents</SelectItem>
                  <SelectItem value="pending">Pending Only</SelectItem>
                  <SelectItem value="approved">Approved</SelectItem>
                  <SelectItem value="rejected">Rejected</SelectItem>
                </SelectContent>
              </Select>
              <Button variant="outline" className="border-primary text-primary hover:bg-blue-50">
                <Download className="h-4 w-4 mr-2" />
                Export
              </Button>
            </div>
          </div>
        </div>

        {filteredDocuments.length === 0 ? (
          <CardContent className="p-6 text-center">
            <FileText className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h4 className="text-lg font-medium text-gray-900 mb-2">No documents found</h4>
            <p className="text-gray-600">
              {statusFilter === "all" ? 
                "No documents have been uploaded yet." : 
                `No ${statusFilter} documents found.`
              }
            </p>
          </CardContent>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Client
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Document
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Type
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Uploaded
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredDocuments.map((document: any) => (
                  <tr key={document.id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <Avatar className="h-8 w-8 mr-3">
                          <AvatarImage src={document.client?.user?.profileImageUrl} />
                          <AvatarFallback className="bg-primary text-white text-sm">
                            {getInitials(document.client?.user?.firstName, document.client?.user?.lastName)}
                          </AvatarFallback>
                        </Avatar>
                        <div>
                          <div className="text-sm font-medium text-gray-900">
                            {document.client?.user?.firstName} {document.client?.user?.lastName}
                          </div>
                          {document.client?.user?.companyName && (
                            <div className="text-sm text-gray-500">
                              {document.client.user.companyName}
                            </div>
                          )}
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <FileText className="h-4 w-4 text-primary mr-2" />
                        <span className="text-sm text-gray-900">{document.originalName}</span>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {getDocumentTypeName(document.documentType)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {formatDistanceToNow(new Date(document.uploadedAt), { addSuffix: true })}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {getStatusBadge(document.status)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      <div className="flex items-center space-x-2">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => setSelectedDocument(document)}
                          className="text-primary hover:text-blue-700"
                        >
                          <Eye className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleDownload(document)}
                          className="text-gray-400 hover:text-gray-600"
                        >
                          <Download className="h-4 w-4" />
                        </Button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </Card>

      {selectedDocument && (
        <DocumentReviewModal
          document={selectedDocument}
          isOpen={!!selectedDocument}
          onClose={() => setSelectedDocument(null)}
        />
      )}
    </>
  );
}

client/src/components/documents/document.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { DOCUMENT_TYPES } from "@/lib/constants";
import DocumentReviewModal from "./document-review-modal";
import { FileText, Download, Eye, RotateCcw, Trash2 } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import type { Document } from "@shared/schema";

export default function DocumentList() {
  const [selectedDocument, setSelectedDocument] = useState<Document | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: documents = [], isLoading } = useQuery<Document[]>({
    queryKey: ["/api/documents"],
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/documents/${id}`);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Document deleted successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/documents"] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleDownload = async (document: Document) => {
    try {
      const response = await fetch(`/api/documents/${document.id}/download`, {
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error('Download failed');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = window.document.createElement('a');
      a.href = url;
      a.download = document.originalName;
      window.document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      window.document.body.removeChild(a);
    } catch (error) {
      toast({
        title: "Download Failed",
        description: "Unable to download the document.",
        variant: "destructive",
      });
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'APPROVED':
        return (
          <Badge className="bg-green-100 text-green-800 border-green-200">
            Approved
          </Badge>
        );
      case 'PENDING':
        return (
          <Badge className="bg-yellow-100 text-yellow-800 border-yellow-200">
            Pending
          </Badge>
        );
      case 'REJECTED':
        return (
          <Badge className="bg-red-100 text-red-800 border-red-200">
            Rejected
          </Badge>
        );
      default:
        return (
          <Badge variant="secondary">
            {status}
          </Badge>
        );
    }
  };

  const getDocumentTypeName = (type: string) => {
    return DOCUMENT_TYPES[type as keyof typeof DOCUMENT_TYPES]?.name || type;
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="animate-pulse space-y-4">
            <div className="h-4 bg-gray-200 rounded w-1/4"></div>
            <div className="space-y-3">
              {[...Array(3)].map((_, i) => (
                <div key={i} className="h-16 bg-gray-200 rounded"></div>
              ))}
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <>
      <Card className="hover:shadow-lg transition-shadow">
        <div className="p-6 border-b border-gray-200">
          <h3 className="text-xl font-semibold text-gray-900">Your Documents</h3>
        </div>

        {documents.length === 0 ? (
          <CardContent className="p-6 text-center">
            <FileText className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h4 className="text-lg font-medium text-gray-900 mb-2">No documents uploaded</h4>
            <p className="text-gray-600">Upload your first compliance document to get started.</p>
          </CardContent>
        ) : (
          <div className="divide-y divide-gray-200">
            {documents.map((document) => (
              <div key={document.id} className="p-6 hover:bg-gray-50 transition-colors">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-4">
                    <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                      <FileText className="h-5 w-5 text-primary" />
                    </div>
                    <div>
                      <h4 className="font-medium text-gray-900">{document.originalName}</h4>
                      <p className="text-sm text-gray-500">
                        {getDocumentTypeName(document.documentType)}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center space-x-4">
                    {getStatusBadge(document.status)}
                    <span className="text-sm text-gray-500">
                      {formatDistanceToNow(new Date(document.uploadedAt), { addSuffix: true })}
                    </span>
                    <div className="flex items-center space-x-2">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => setSelectedDocument(document)}
                        className="text-gray-400 hover:text-gray-600"
                      >
                        <Eye className="h-4 w-4" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleDownload(document)}
                        className="text-gray-400 hover:text-gray-600"
                      >
                        <Download className="h-4 w-4" />
                      </Button>
                      {document.status === 'REJECTED' && (
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => {/* Handle re-upload */}}
                          className="text-blue-500 hover:text-blue-700"
                        >
                          <RotateCcw className="h-4 w-4" />
                        </Button>
                      )}
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => deleteMutation.mutate(document.id)}
                        disabled={deleteMutation.isPending}
                        className="text-red-500 hover:text-red-700"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </div>

                {/* Rejection Reason */}
                {document.status === 'REJECTED' && document.rejectionReason && (
                  <div className="mt-3 ml-14">
                    <div className="bg-red-50 border border-red-200 rounded-lg p-3">
                      <p className="text-sm text-red-800">
                        <strong>Rejection Reason:</strong> {document.rejectionReason}
                      </p>
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </Card>

      {selectedDocument && (
        <DocumentReviewModal
          document={selectedDocument}
          isOpen={!!selectedDocument}
          onClose={() => setSelectedDocument(null)}
        />
      )}
    </>
  );
}

import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { DOCUMENT_TYPES } from "@/lib/constants";
import { FileText, Download } from "lucide-react";
import { format } from "date-fns";

interface DocumentReviewModalProps {
  document: any;
  isOpen: boolean;
  onClose: () => void;
}

export default function DocumentReviewModal({ document, isOpen, onClose }: DocumentReviewModalProps) {
  const [reviewStatus, setReviewStatus] = useState("");
  const [comments, setComments] = useState("");
  const { toast } = useToast();
  const { user } = useAuth();
  const queryClient = useQueryClient();

  const reviewMutation = useMutation({
    mutationFn: async (data: { status: string; rejectionReason?: string }) => {
      return apiRequest("PUT", `/api/documents/${document.id}/status`, data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Document review submitted successfully.",
      });
      onClose();
      queryClient.invalidateQueries({ queryKey: ["/api/documents"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/stats"] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmitReview = () => {
    if (!reviewStatus) {
      toast({
        title: "Missing Selection",
        description: "Please select a review decision.",
        variant: "destructive",
      });
      return;
    }

    if (reviewStatus === 'REJECTED' && !comments.trim()) {
      toast({
        title: "Missing Comments",
        description: "Please provide a reason for rejection.",
        variant: "destructive",
      });
      return;
    }

    reviewMutation.mutate({
      status: reviewStatus,
      rejectionReason: reviewStatus === 'REJECTED' ? comments : undefined,
    });
  };

  const handleDownload = async () => {
    try {
      const response = await fetch(`/api/documents/${document.id}/download`, {
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error('Download failed');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = window.document.createElement('a');
      a.href = url;
      a.download = document.originalName;
      window.document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      window.document.body.removeChild(a);
    } catch (error) {
      toast({
        title: "Download Failed",
        description: "Unable to download the document.",
        variant: "destructive",
      });
    }
  };

  const getDocumentTypeName = (type: string) => {
    return DOCUMENT_TYPES[type as keyof typeof DOCUMENT_TYPES]?.name || type;
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-auto">
        <DialogHeader>
          <DialogTitle>Document Review</DialogTitle>
        </DialogHeader>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 py-4">
          <div>
            <h4 className="font-medium text-gray-900 mb-4">Document Information</h4>
            <dl className="space-y-3">
              {document.client && (
                <div>
                  <dt className="text-sm font-medium text-gray-500">Client</dt>
                  <dd className="text-sm text-gray-900">
                    {document.client.user.firstName} {document.client.user.lastName}
                    {document.client.user.companyName && (
                      <span className="text-gray-500"> - {document.client.user.companyName}</span>
                    )}
                  </dd>
                </div>
              )}
              <div>
                <dt className="text-sm font-medium text-gray-500">Document Type</dt>
                <dd className="text-sm text-gray-900">{getDocumentTypeName(document.documentType)}</dd>
              </div>
              <div>
                <dt className="text-sm font-medium text-gray-500">Filename</dt>
                <dd className="text-sm text-gray-900">{document.originalName}</dd>
              </div>
              <div>
                <dt className="text-sm font-medium text-gray-500">Upload Date</dt>
                <dd className="text-sm text-gray-900">
                  {format(new Date(document.uploadedAt), "MMMM d, yyyy 'at' h:mm a")}
                </dd>
              </div>
              <div>
                <dt className="text-sm font-medium text-gray-500">File Size</dt>
                <dd className="text-sm text-gray-900">
                  {(document.fileSize / 1024 / 1024).toFixed(2)} MB
                </dd>
              </div>
              <div>
                <dt className="text-sm font-medium text-gray-500">Current Status</dt>
                <dd className="text-sm text-gray-900 capitalize">{document.status.toLowerCase()}</dd>
              </div>
            </dl>
          </div>

          <div>
            <h4 className="font-medium text-gray-900 mb-4">Document Preview</h4>
            <div className="bg-gray-100 rounded-lg h-64 flex items-center justify-center">
              <div className="text-center">
                <FileText className="h-16 w-16 text-primary mx-auto mb-4" />
                <p className="text-gray-600 text-sm mb-4">
                  {document.mimeType === 'application/pdf' ? 'PDF Document' : 'Image File'}
                </p>
                <Button
                  onClick={handleDownload}
                  variant="outline"
                  className="text-primary border-primary hover:bg-blue-50"
                >
                  <Download className="h-4 w-4 mr-2" />
                  Download Full Document
                </Button>
              </div>
            </div>
          </div>
        </div>

        {user?.role === 'ADMIN' && (
          <div className="pt-6 border-t border-gray-200">
            <h4 className="font-medium text-gray-900 mb-4">Review Action</h4>
            <div className="space-y-4">
              <div>
                <Label className="text-sm font-medium text-gray-700 mb-2 block">
                  Review Decision
                </Label>
                <Select value={reviewStatus} onValueChange={setReviewStatus}>
                  <SelectTrigger className="w-full">
                    <SelectValue placeholder="Select action..." />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="APPROVED">Approve Document</SelectItem>
                    <SelectItem value="REJECTED">Reject Document</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="text-sm font-medium text-gray-700 mb-2 block">
                  Comments {reviewStatus === 'REJECTED' && "(Required for rejection)"}
                </Label>
                <Textarea
                  value={comments}
                  onChange={(e) => setComments(e.target.value)}
                  className="h-24"
                  placeholder="Enter reason for rejection or approval notes..."
                />
              </div>

              <div className="flex items-center justify-end space-x-3">
                <Button
                  variant="outline"
                  onClick={onClose}
                  disabled={reviewMutation.isPending}
                >
                  Cancel
                </Button>
                <Button
                  onClick={handleSubmitReview}
                  disabled={reviewMutation.isPending}
                  className="bg-primary hover:bg-blue-700"
                >
                  {reviewMutation.isPending ? "Submitting..." : "Submit Review"}
                </Button>
              </div>
            </div>
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

import { useState, useCallback } from "react";
import { useDropzone } from "react-dropzone";
import { useMutation, useQueryClient, useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Upload, FileText, CheckCircle, MessageSquare, Clock } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { DOCUMENT_TYPES } from "@/lib/constants";

interface DocumentUploadZoneProps {
  documentType: string;
  documentInfo: any;
  existingDoc?: any;
  onUpload: (file: File, documentType: string) => void;
  isUploading: boolean;
}

function DocumentUploadZone({ 
  documentType, 
  documentInfo, 
  existingDoc,
  onUpload, 
  isUploading 
}: DocumentUploadZoneProps) {
  const [isDragOver, setIsDragOver] = useState(false);

  const onDrop = useCallback((acceptedFiles: File[]) => {
    if (acceptedFiles.length > 0 && !existingDoc) {
      onUpload(acceptedFiles[0], documentType);
    }
  }, [onUpload, documentType, existingDoc]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'image/jpeg': ['.jpg', '.jpeg'],
      'image/png': ['.png']
    },
    maxFiles: 1,
    disabled: !!existingDoc
  });

  const getStatusBadge = () => {
    if (!existingDoc) return null;

    switch (existingDoc.status) {
      case 'APPROVED':
        return <Badge className="bg-green-100 text-green-800">Approved</Badge>;
      case 'PENDING':
        return <Badge className="bg-yellow-100 text-yellow-800">Pending Review</Badge>;
      case 'REJECTED':
        return <Badge className="bg-red-100 text-red-800">Rejected</Badge>;
      case 'NEEDS_REVISION':
        return <Badge className="bg-orange-100 text-orange-800">Needs Revision</Badge>;
      default:
        return <Badge className="bg-gray-100 text-gray-800">Unknown</Badge>;
    }
  };

  return (
    <Card className={`w-full transition-all duration-200 h-full ${
      existingDoc ? 'bg-gray-50 border-gray-200' : 
      isDragActive ? 'border-primary border-2 bg-blue-50' : 
      'hover:border-gray-300 hover:shadow-md'
    }`}>
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="text-sm font-medium text-gray-900">
            {documentInfo.name}
          </CardTitle>
          {getStatusBadge()}
        </div>
        {documentInfo.required && (
          <Badge variant="outline" className="text-xs w-fit">
            Required
          </Badge>
        )}
      </CardHeader>

      <CardContent>
        <div
          {...getRootProps()}
          className={`
            relative min-h-[160px] border-2 border-dashed rounded-lg p-6 text-center cursor-pointer transition-all duration-200
            ${existingDoc ? 
              'border-gray-200 bg-gray-50 cursor-not-allowed' : 
              isDragActive ? 
                'border-primary bg-blue-50 border-solid' : 
                'border-gray-300 hover:border-gray-400 hover:bg-gray-50'
            }
          `}
        >
          <input {...getInputProps()} />

          {existingDoc ? (
            // Uploaded state
            <div className="flex flex-col items-center justify-center space-y-2">
              <CheckCircle className="h-8 w-8 text-green-500" />
              <p className="text-sm font-medium text-gray-700">
                {existingDoc.originalName}
              </p>
              <p className="text-xs text-gray-500">
                Uploaded {new Date(existingDoc.uploadedAt).toLocaleDateString()}
              </p>

              {/* Comments section */}
              <div className="mt-3 flex items-center space-x-2">
                <Button 
                  variant="ghost" 
                  size="sm" 
                  className="text-blue-600 hover:text-blue-700"
                  onClick={(e) => {
                    e.stopPropagation();
                    // TODO: Open comments dialog
                    console.log('Comments clicked for', existingDoc.originalName);
                  }}
                >
                  <MessageSquare className="h-3 w-3 mr-1" />
                  Comments
                </Button>
              </div>

              {existingDoc.reviewNotes && (
                <div className="mt-2 p-2 bg-yellow-50 rounded text-xs text-gray-700 border border-yellow-200">
                  <strong>Review Notes:</strong> {existingDoc.reviewNotes}
                </div>
              )}
            </div>
          ) : isUploading ? (
            // Uploading state
            <div className="flex flex-col items-center justify-center space-y-2">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
              <p className="text-sm font-medium text-gray-700">Uploading...</p>
            </div>
          ) : (
            // Upload state
            <div className="flex flex-col items-center justify-center space-y-2">
              <Upload className={`h-8 w-8 ${isDragActive ? 'text-primary' : 'text-gray-400'}`} />
              <div className="text-center">
                <p className="text-sm font-medium text-gray-700">
                  {isDragActive ? 'Drop file here' : 'Drag & Drop files here or'}
                </p>
                <Button 
                  variant="outline" 
                  size="sm" 
                  className="mt-2 bg-orange-500 hover:bg-orange-600 text-white border-orange-500"
                  type="button"
                >
                  Browse for files
                </Button>
              </div>
            </div>
          )}
        </div>

        <div className="mt-3 text-xs text-gray-500">
          <p>{documentInfo.description}</p>
          <p className="mt-1">
            Accepted: {documentInfo.acceptedTypes.join(", ")} (max {documentInfo.maxSize}MB)
          </p>
        </div>
      </CardContent>
    </Card>
  );
}

export default function DocumentUpload() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [uploadingTypes, setUploadingTypes] = useState<Set<string>>(new Set());

  // Get existing documents to show completed uploads
  const { data: documents = [] } = useQuery<any[]>({
    queryKey: ["/api/documents"],
  });

  const uploadMutation = useMutation({
    mutationFn: async ({ file, documentType }: { file: File; documentType: string }) => {
      const formData = new FormData();
      formData.append("document", file);
      formData.append("documentType", documentType);

      const response = await apiRequest("POST", "/api/documents", formData);
      return response.json();
    },
    onSuccess: (data, variables) => {
      toast({
        title: "Document uploaded successfully!",
        description: `${DOCUMENT_TYPES[variables.documentType as keyof typeof DOCUMENT_TYPES]?.name} has been submitted for review.`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/documents"] });
      setUploadingTypes(prev => {
        const newSet = new Set(prev);
        newSet.delete(variables.documentType);
        return newSet;
      });
    },
    onError: (error: Error, variables) => {
      toast({
        title: "Upload failed",
        description: error.message,
        variant: "destructive",
      });
      setUploadingTypes(prev => {
        const newSet = new Set(prev);
        newSet.delete(variables.documentType);
        return newSet;
      });
    },
  });

  const handleUpload = (file: File, documentType: string) => {
    setUploadingTypes(prev => new Set(prev).add(documentType));
    uploadMutation.mutate({ file, documentType });
  };

  // Create a map of existing documents by type
  const existingDocsByType = documents.reduce((acc, doc) => {
    acc[doc.documentType] = doc;
    return acc;
  }, {} as Record<string, any>);

  return (
    <div className="w-full h-full min-h-screen bg-white">
      <div className="h-full p-8">
        <div className="text-center mb-10">
          <h2 className="text-3xl font-bold text-gray-900">Document Upload</h2>
          <p className="text-gray-600 mt-3 text-lg">
            Upload your compliance documents. Each document type has its own upload area.
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-8 pb-8">
          {Object.entries(DOCUMENT_TYPES).map(([key, docInfo]) => (
            <DocumentUploadZone
              key={key}
              documentType={key}
              documentInfo={docInfo}
              existingDoc={existingDocsByType[key]}
              onUpload={handleUpload}
              isUploading={uploadingTypes.has(key)}
            />
          ))}
        </div>

        {documents.length > 0 && (
          <div className="mt-12 p-6 bg-blue-50 rounded-lg">
            <h3 className="font-semibold text-blue-900 mb-4 text-lg">Upload Progress</h3>
            <div className="flex flex-wrap items-center gap-6">
              <div className="flex items-center">
                <CheckCircle className="h-5 w-5 text-green-500 mr-2" />
                <span className="text-sm text-gray-700 font-medium">
                  {documents.length} documents uploaded
                </span>
              </div>
              <div className="flex items-center">
                <Clock className="h-5 w-5 text-yellow-500 mr-2" />
                <span className="text-sm text-gray-700 font-medium">
                  {documents.filter(d => d.status === 'PENDING').length} pending review
                </span>
              </div>
              <div className="flex items-center">
                <CheckCircle className="h-5 w-5 text-green-500 mr-2" />
                <span className="text-sm text-gray-700 font-medium">
                  {documents.filter(d => d.status === 'APPROVED').length} approved
                </span>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

